mapper.select:
select p.*, COUNT(pu.product_id) productQuantity, pu.buy_price from sale_product p left join sale_product_user pu on pu.product_id = p.id left join usc_user u on pu.user_id = u.id where pu.is_deleted = 0 AND u.id = #{userId} AND pu.status = 0
AND p.collections_id = #{collectionsId}
group by pu.product_id order by p.sort DESC

select p.*, pu.buy_price from sale_product p left join sale_product_user pu on pu.product_id = p.id left join usc_user u on pu.user_id = u.id where pu.is_deleted = 0 AND u.id = #{userId} AND pu.status = 0
AND pu.product_id = #{productId}
order by p.sort DESC

select p.*, pu.buy_price, pu.id userProductId from sale_product p left join sale_product_user pu on pu.product_id = p.id left join usc_user u on pu.user_id = u.id where pu.is_deleted = 0 AND u.id = #{userId} AND pu.status = #{status}
AND pu.product_id = #{productId}
order by p.sort DESC
